package day02;

public class SwitchTest {

  public static void main(String[] args) {
    // TODO Auto-generated method stub
    int k = 1;
    switch(k) {
    case 1:
      System.out.println("1 입니다.");
      break;
    case 2:
      System.out.println("2 입니다.");
      break;
    case 3:
      System.out.println("2 입니다.");
      break;
    case 4:
      System.out.println("2 입니다.");
      break;
     default:
       System.out.println("1부터 4까지 입력해야 합니다.");
       break;
    }
  }

}
